CREATE TABLE IF NOT EXISTS `c_attachments` (`a_id` int(6) NOT NULL AUTO_INCREMENT, `user_id` int(8) DEFAULT NULL, `date` int(10) DEFAULT NULL, `attach_filename` varchar(50) DEFAULT NULL, `attach_type` varchar(50) DEFAULT NULL, `attach_clicks` int(8) DEFAULT NULL, `post_id` int(10) DEFAULT NULL, `pm_id` int(10) DEFAULT NULL, PRIMARY KEY (`a_id`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `c_bbcodes` (`id` int(2) NOT NULL AUTO_INCREMENT, `reg_x` varchar(255) NOT NULL, `html` text NOT NULL, `name` varchar(20) NOT NULL, `active` int(1) NOT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `c_config` (`id` int(5) NOT NULL AUTO_INCREMENT, `index` varchar(40) NOT NULL, `value` text, PRIMARY KEY (`id`) ) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `c_emoticons` (`id` int(11) NOT NULL AUTO_INCREMENT, `shortcut` varchar(10) DEFAULT NULL, `filename` varchar(30) DEFAULT NULL, `display` int(1) DEFAULT NULL, `emoticon_set` varchar(20) DEFAULT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `c_events` (`e_id` int(11) NOT NULL AUTO_INCREMENT, `title` varchar(100) NOT NULL, `type` varchar(10) NOT NULL, `day` int(2) NOT NULL, `month` int(2) NOT NULL, `year` int(4) NOT NULL, `timestamp` int(10) NOT NULL, `text` text, PRIMARY KEY (`e_id`) ) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `c_follow` (`id` int(11) NOT NULL AUTO_INCREMENT, `follower` int(8) DEFAULT NULL, `following` int(8) DEFAULT NULL, `date` int(10) DEFAULT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `c_help` (`h_id` int(11) NOT NULL AUTO_INCREMENT, `title` varchar(45) DEFAULT NULL, `short_desc` varchar(255) DEFAULT NULL, `content` text, PRIMARY KEY (`h_id`) ) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `c_languages` (`l_id` int(2) NOT NULL AUTO_INCREMENT, `name` varchar(45) NOT NULL, `directory` varchar(10) NOT NULL, `author_name` varchar(50) NOT NULL, `author_email` varchar(50) NOT NULL, `active` int(1) NOT NULL, `default` int(1) NOT NULL, PRIMARY KEY (`l_id`) ) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `c_logs` (`log_id` int(11) NOT NULL AUTO_INCREMENT, `member_id` int(8) NOT NULL, `time` int(10) NOT NULL, `act` varchar(250) DEFAULT NULL, `ip_address` varchar(46) DEFAULT NULL, PRIMARY KEY (`log_id`) ) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `c_members` (`m_id` int(8) NOT NULL AUTO_INCREMENT, `username` varchar(45) NOT NULL, `password` varchar(128) NOT NULL, `email` varchar(50) NOT NULL, `hide_email` int(1) DEFAULT NULL, `ip_address` varchar(46) DEFAULT NULL, `joined` int(10) DEFAULT NULL, `usergroup` int(2) DEFAULT NULL, `member_title` varchar(40) DEFAULT NULL, `location` varchar(50) DEFAULT NULL, `profile` text, `gender` varchar(1) DEFAULT NULL, `b_day` int(2) DEFAULT NULL, `b_month` int(2) DEFAULT NULL, `b_year` int(4) DEFAULT NULL, `photo` varchar(40) DEFAULT NULL, `photo_type` varchar(10) DEFAULT NULL, `website` varchar(60) DEFAULT NULL, `im_windowslive` varchar(50) DEFAULT NULL, `im_skype` varchar(50) DEFAULT NULL, `im_facebook` varchar(50) DEFAULT NULL, `im_twitter` varchar(50) DEFAULT NULL, `im_yim` varchar(50) DEFAULT NULL, `im_aol` varchar(50) DEFAULT NULL, `posts` int(9) DEFAULT NULL, `lastpost_date` int(10) DEFAULT NULL, `signature` text, `template` varchar(10) DEFAULT NULL, `language` varchar(10) DEFAULT NULL, `warn_level` int(1) DEFAULT NULL, `warn_date` int(10) DEFAULT NULL, `last_activity` int(10) DEFAULT NULL, `time_offset` varchar(5) DEFAULT NULL, `dst` int(1) DEFAULT NULL, `show_email` int(1) DEFAULT NULL, `show_birthday` int(1) DEFAULT NULL, `show_gender` int(1) DEFAULT NULL, `token` varchar(128) DEFAULT NULL, PRIMARY KEY (`m_id`) ) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `c_messages` (`pm_id` int(8) NOT NULL AUTO_INCREMENT, `from_id` int(8) NOT NULL, `to_id` int(8) NOT NULL, `subject` varchar(35) NOT NULL, `status` int(1) NOT NULL, `sent_date` int(10) NOT NULL, `message` text NOT NULL, `read_date` int(10) DEFAULT NULL, `attach_id` int(6) DEFAULT NULL, `parent_pm` int(8) DEFAULT NULL, PRIMARY KEY (`pm_id`) ) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `c_posts` (`p_id` int(9) NOT NULL AUTO_INCREMENT, `author_id` int(8) NOT NULL, `thread_id` int(8) NOT NULL, `post_date` int(10) NOT NULL, `attach_id` int(6) DEFAULT NULL, `attach_clicks` int(10) DEFAULT NULL, `ip_address` varchar(46) NOT NULL, `post` text NOT NULL, `edit_time` int(10) DEFAULT NULL, `edit_author` int(8) DEFAULT NULL, `best_answer` int(1) NOT NULL, `first_post` int(1) NOT NULL, PRIMARY KEY (`p_id`), FULLTEXT KEY `post` (`post`) ) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `c_ranks` (`id` int(11) NOT NULL AUTO_INCREMENT, `title` varchar(20) DEFAULT NULL, `min_posts` int(5) DEFAULT NULL, `pips` int(1) DEFAULT NULL, `image` varchar(20) DEFAULT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `c_reports` (`rp_id` int(11) NOT NULL AUTO_INCREMENT, `description` text, `date` int(10) NOT NULL, `sender_id` int(9) NOT NULL, `ip_address` varchar(15) NOT NULL, `post_id` int(9) NOT NULL, `thread_id` int(9) NOT NULL, `referer` varchar(255) NOT NULL, PRIMARY KEY (`rp_id`) ) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `c_rooms` (`r_id` int(3) NOT NULL AUTO_INCREMENT, `name` varchar(100) DEFAULT NULL, `description` text, `url` varchar(100) DEFAULT NULL, `order_n` int(3) DEFAULT NULL, `lastpost_date` int(10) DEFAULT NULL, `lastpost_thread` int(8) DEFAULT NULL, `lastpost_member` int(8) DEFAULT NULL, `invisible` int(1) DEFAULT NULL, `rules_title` varchar(50) DEFAULT NULL, `rules_text` text, `rules_visible` int(1) DEFAULT NULL, `read_only` int(1) DEFAULT NULL, `password` varchar(45) DEFAULT NULL, `upload` int(1) DEFAULT NULL, `perm_view` varchar(255) DEFAULT NULL, `perm_post` varchar(255) DEFAULT NULL, `perm_reply` varchar(255) DEFAULT NULL, PRIMARY KEY (`r_id`) ) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `c_sessions` (`s_id` varchar(60) NOT NULL, `member_id` int(11) DEFAULT NULL, `username` varchar(50) DEFAULT NULL, `ip_address` varchar(46) DEFAULT NULL, `browser` varchar(200) DEFAULT NULL, `activity_time` int(10) DEFAULT NULL, `usergroup` int(2) DEFAULT NULL, `anonymous` int(1) DEFAULT NULL, `location_type` varchar(30) DEFAULT NULL, `location_id` int(11) DEFAULT NULL, `location_room_id` int(11) DEFAULT NULL, PRIMARY KEY (`s_id`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `c_stats` (`id` int(1) NOT NULL AUTO_INCREMENT, `member_count` int(8) DEFAULT NULL, `total_posts` int(10) DEFAULT NULL, `total_threads` int(10) DEFAULT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `c_templates` (`tpl_id` int(11) NOT NULL AUTO_INCREMENT, `name` varchar(45) NOT NULL, `directory` varchar(10) NOT NULL, `active` int(1) NOT NULL, `author_name` varchar(50) NOT NULL, `author_email` varchar(50) NOT NULL, `css_file` varchar(20) NOT NULL, PRIMARY KEY (`tpl_id`) ) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `c_threads` (`t_id` int(11) NOT NULL AUTO_INCREMENT, `title` varchar(250) NOT NULL, `author_member_id` int(8) NOT NULL, `replies` int(9) NOT NULL, `views` int(9) NOT NULL, `start_date` int(10) NOT NULL, `room_id` int(3) NOT NULL, `tags` varchar(255) DEFAULT NULL, `announcement` int(1) DEFAULT NULL, `lastpost_date` int(10) DEFAULT NULL, `lastpost_member_id` int(8) DEFAULT NULL, `moved_to` int(3) DEFAULT NULL, `locked` int(1) DEFAULT NULL, `approved` int(1) DEFAULT NULL, `with_bestanswer` int(1) DEFAULT NULL, PRIMARY KEY (`t_id`), FULLTEXT KEY `title` (`title`) ) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `c_usergroups` (`g_id` int(2) NOT NULL AUTO_INCREMENT, `name` varchar(40) NOT NULL, `preffix` varchar(20) DEFAULT NULL, `suffix` varchar(20) DEFAULT NULL, `color` varchar(7) DEFAULT NULL, `view_board` int(1) DEFAULT NULL, `post_new_threads` int(1) DEFAULT NULL, `reply_threads` int(1) DEFAULT NULL, `edit_own_threads` int(1) DEFAULT NULL, `edit_own_posts` int(1) DEFAULT NULL, `delete_own_posts` int(1) DEFAULT NULL, `can_attach` int(1) DEFAULT NULL, `access_offline` int(1) DEFAULT NULL, `post_html` int(1) DEFAULT NULL, `avoid_flood` int(1) DEFAULT NULL, `admin_cp` int(1) DEFAULT NULL, `max_pm_storage` int(8) DEFAULT NULL, `stock` int(1) DEFAULT NULL, PRIMARY KEY (`g_id`) ) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;